discussion.pre:
if (sz > size())
  insert(end(), sz-size(), c);
else if (sz < size())
  erase(begin()+sz, end());
else
  ;                           //  do nothing

resolution.pre:
if (sz > size())
  insert(end(), sz-size(), c);
else if (sz < size())
{
  iterator i = begin();
  advance(i, sz);
  erase(i, end());
}
