#include <algorithm>
#include <iostream>
#include <iterator>
#include <utility>

struct F
{
  void operator()(int n)
  {
    std::cout << n << std::endl;
    ++count;
  }
  int count;
} f;

int main()
{
  int arr[] = { 1, 2, 3 };
  auto&& result = std::for_each<int *, F &&>( // undefined?
    std::begin(arr),
    std::end(arr),
    std::move(f));
  std::cout << "count: " << result.count << std::endl;
}
